class Cards extends Component {
    state = {
        clicked : false,
    };

    handleClick = ()=> {
        console.log('im clicked')
        this.setState({clicked: true})
    };

    render(){
        return (
        <div className="card" onClick = {this.handleClick}>
            <img  src={this.props.image}/>
            {this.state.clicked}
        </div>
        )
    }

}
export default Cards;
