//     )
// }


class Cards extends Component {
    state = {
        clicked : false,
    };

    handleClick = ()=> {
        console.log('im clicked')
        this.setState({clicked: true})
    };

    render(){
        return (

            <div className="card">
            <div className="img-container">
                <img alt={this.state.name} src={this.state.image} onClick = {clicked}/>
            </div>
            </div>
        )
    }

}
export default Cards;
