     console.log('1x ');
    this.setState({clicked: this.state.clicked + 1})
    this.setState({correctScore: this.state.correctScore + 1})
    }
    if(btnType<2){
      console.log('2x')
      this.setState({clicked:''})
    }
   }

};

  render() {

    return (
    <div>
      <Navbar/>
        <Jumbotron/>
        {this.state.friends.map(friend => (
          <Cards
            id={friend.id}
            key= {friend.id}
            image={friend.image}
            handleClick = {this.handleClick}
            />
        ))}

      </div>

    )}
      }



export default App;
